import { extendTheme, theme as CharaTheme } from "@chakra-ui/react";

export const theme = extendTheme({
  colors: {
    primary: {
      100: "#27AE60",
    },
    secondary: {
      100: "#EB5757",
    },
    grey: {
      600: "#333333",
      300: "#828282",
      100: "#E0E0E0",
      0: "#F5F5F5",
    },
    negative: {
      100: "#E60000",
    },
    warning: {
      100: "#FFCD07",
    },
    sucess: {
      100: "#168821",
    },
    information: {
      100: "#155BCB",
    },
    fonts: {
      heading: "Inter",
      body: "Inter",
    },
  },
});
