import { Flex, Text, Grid, VStack, Box } from "@chakra-ui/layout";

import React, { useState } from "react";
import { useForm } from "react-hook-form";
import { FaEnvelope, FaLock } from "react-icons/fa";
import { Input } from "../../components/Form/form";

import { yupResolver } from "@hookform/resolvers/yup";

import * as yup from "yup";
import { Button } from "@chakra-ui/button";
import { useAuth } from "../../Provider/authToken";
import { useHistory } from "react-router";
import { LoginInfo } from "./LoginInfo";

const singInSchema = yup.object().shape({
  email: yup.string().required("Email obrigatório").email("email inválido"),
  password: yup.string().required("Senha Obrigatória"),
});

interface SignInData {
  email: string;
  password: string;
}

export const Login = () => {
  const [loading, setLoading] = useState(false);
  const { signIn } = useAuth();
  const history = useHistory();
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(singInSchema),
  });

  const handleSignIn = (data: SignInData) => {
    setLoading(true);
    console.log(data);
    signIn(data)
      .then((_) => setLoading(false))
      .catch((err) => console.log(err));
  };

  return (
    <Flex
      alignItems="center"
      justifyContent="center"
      padding="10px 15px"
      height={["auto", "auto", "100vh", "100vh"]}
    >
      <Flex
        w={["100%", "100%", "90%", "65%"]}
        flexDirection={["column", "column", "row", "row"]}
        justifyContent="center"
        alignItems="center"
      >
        <LoginInfo />
        <Grid
          as="form"
          mt="4"
          w={["100%", "100%", "60%", "50%"]}
          padding="30px 15px"
          border="3px solid"
          borderColor="gray.100"
          bg="white"
          onSubmit={handleSubmit(handleSignIn)}
          color="gray.900"
        >
          <Text as="h2" fontWeight="bold">
            Login
          </Text>
          <VStack mt="6" spacing="5">
            <Box w="100%">
              <Input
                placeholder="Digite seu E-mail"
                icon={FaEnvelope}
                label="E-mail"
                {...register("email")}
                type="email"
                error={errors.email}
              />
              {!errors.email && (
                <Text ml="1" mt="1" color="gray.300">
                  Exemplo : nome@email.com
                </Text>
              )}
            </Box>
            <Input
              placeholder="Digite sua Senha"
              icon={FaLock}
              type="password"
              label="Senha"
              error={errors.password}
              {...register("password")}
            />
          </VStack>
          <VStack mt="4" spacing="5">
            <Button
              isLoading={loading}
              type="submit"
              bg="primary.200"
              color="white"
              mt="20px"
              _hover={{ background: "primary.100" }}
            >
              Entrar
            </Button>
            <Text textColor="gray.400" textAlign="center">
              Crie sua conta para saborear muitas delicias e matar sua fome!
            </Text>
            <Button
              bg="gray.100"
              color="gray.400"
              mt="20px"
              _hover={{ background: "gray.200" }}
            >
              Cadastrar
            </Button>
          </VStack>
        </Grid>
      </Flex>
    </Flex>
  );
};
